      </div>
      <MeditationCalendar v-if="showCalendar && !meditationActive" :meditations="meditations" @close="showCalendar = false" />
      <SessionNotes v-if="showNotes" :duration="completedMeditationDuration" @save="saveSessionNotes" @skip="skipSessionNotes" @close="showNotes = false" />
      <BreathingExercise v-if="showBreathing" @close="showBreathing = false" />
      <EmotionTracker v-if="showEmotions" @close="showEmotions = false" />
      <GratitudeJournal v-if="showGratitude" @close="showGratitude = false" />
      <ZenPhilosophy v-if="showPhilosophy" @close="showPhilosophy = false" />
      <EightfoldPath v-if="showEightfoldPath" @close="showEightfoldPath = false" />

      <div v-if="!meditationActive" class="zen-main">
        <div class="zen-center">
        <span :class="['zen-phrase', { dimmed: showAudioConfig || showBellConfig }]">{{ currentPhrase }}</span>
        <span :class="['zen-loader', { dimmed: showAudioConfig || showBellConfig }]">
          <svg width="32" height="32" viewBox="0 0 32 32">
            <rect x="10" y="15" width="12" height="2" rx="1" fill="#F0F8FF">
              <animateTransform attributeName="transform" type="rotate" from="0 16 16" to="360 16 16" dur="2.5s" repeatCount="indefinite"/>
            </rect>
          </svg>
        </span>
        <div v-if="!meditationActive" class="meditation-control-bar">
          <button
            v-for="duration in [1, 5, 10, 15, 20, 30]"
            :key="duration"
            :class="['duration-btn', { active: selectedDuration === duration && !isCustomDuration }]"
            @click="selectPresetDuration(duration)"
            :aria-label="`Set meditation duration to ${duration} minutes`"
          >
            {{ duration }}
          </button>
          <button
            v-if="!isCustomDuration"
            :class="['duration-btn', 'custom-btn']"
            @click="enableCustomDuration"
            :aria-label="'Set custom meditation duration'"
          >
            
          </button>
          <div v-else class="custom-duration-input">
            <input
              ref="customInput"
              type="number"
              v-model.number="customDurationValue"
              @blur="applyCustomDuration"
              @keyup.enter="applyCustomDuration"
              @keyup.esc="cancelCustomDuration"
              min="1"
              max="180"
              placeholder="min"
              aria-label="Enter custom duration in minutes"
            />
            <button class="custom-ok-btn" @click="applyCustomDuration" aria-label="Confirm custom duration">
              <svg width="12" height="12" viewBox="0 0 12 12" fill="none" xmlns="http://www.w3.org/2000/svg">
                <path d="M2 6h8M6 2l4 4-4 4" stroke="currentColor" stroke-width="1.5" stroke-linecap="round" stroke-linejoin="round"/>
              </svg>
            </button>
          </div>
          <button 
            :class="['duration-btn', 'bell-config-btn', { active: bellEnabled }]"
            @click="bellEnabled = !bellEnabled; showBellConfig = bellEnabled"
            :aria-label="bellEnabled ? 'Disable interval bells' : 'Enable interval bells'"
          >
            <svg width="14" height="14" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
              <path d="M12 2C10.9 2 10 2.9 10 4C10 4.6 10.2 5.1 10.6 5.5C8 6.1 6 8.3 6 11V16L4 18V19H20V18L18 16V11C18 8.3 16 6.1 13.4 5.5C13.8 5.1 14 4.6 14 4C14 2.9 13.1 2 12 2ZM10 20C10 21.1 10.9 22 12 22C13.1 22 14 21.1 14 20" stroke="currentColor" stroke-width="1.5" stroke-linecap="round" stroke-linejoin="round"/>
            </svg>
          </button>
          <button 
            :class="['duration-btn', 'audio-config-btn', { active: selectedTrack !== 'silent' || selectedAmbient !== 'none' }]"
            @click="showAudioConfig = !showAudioConfig"
            :aria-label="'Configure meditation audio'"
          >
            <svg width="14" height="14" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
              <path d="M9 18V5l12-2v13M9 18c0 1.657-1.343 3-3 3s-3-1.343-3-3 1.343-3 3-3 3 1.343 3 3zm12-2c0 1.657-1.343 3-3 3s-3-1.343-3-3 1.343-3 3-3 3 1.343 3 3z" stroke="currentColor" stroke-width="1.5" stroke-linecap="round" stroke-linejoin="round"/>
            </svg>
          </button>
          <button class="start-meditation-btn" @click="startMeditation" :aria-label="`Start a ${selectedDuration}-minute meditation session`">
            {{ t('meditation.begin') }}
          </button>
        </div>
        
        <!-- Audio Configuration Panel -->
        <div v-if="showAudioConfig && !meditationActive" class="audio-config-backdrop" @click="showAudioConfig = false"></div>
        <div v-if="showAudioConfig && !meditationActive" class="audio-config-panel">
          <div class="audio-config-header">
            <h3 class="audio-config-panel-title">{{ t('meditation.audio.title') }}</h3>
            <button class="config-close-btn" @click="showAudioConfig = false" aria-label="Close audio settings">
              <svg width="20" height="20" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                <path d="M18 6L6 18M6 6l12 12" stroke="currentColor" stroke-width="2" stroke-linecap="round"/>
              </svg>
            </button>
          </div>
          <div class="audio-config-section">
            <div class="audio-config-title">{{ t('meditation.audio.track') }}</div>
            <div class="audio-track-options">
              <button
                :class="['audio-track-btn', { active: selectedTrack === 'silent' }]"
                @click="selectedTrack = 'silent'"
              >
                {{ t('meditation.audio.silent') }}
              </button>
              <button
                v-for="num in [1, 2, 3, 4, 5]"
                :key="'track' + num"
                :class="['audio-track-btn', { active: selectedTrack === 'track' + num }]"
                @click="selectedTrack = 'track' + num; previewTrack('track' + num)"
              >
                {{ t('meditation.audio.trackLabel') }} {{ num }}
              </button>
            </div>
          </div>
          
          <div class="audio-config-section">
            <div class="audio-config-title">{{ t('meditation.audio.ambient') }}</div>
            <div class="audio-ambient-options">
              <button
                :class="['audio-ambient-btn', { active: selectedAmbient === 'none' }]"
                @click="selectedAmbient = 'none'"
              >
                {{ t('meditation.audio.none') }}
              </button>
              <button
                :class="['audio-ambient-btn', { active: selectedAmbient === 'rain' }]"
                @click="selectedAmbient = 'rain'; previewAmbient('rain')"
              >
                {{ t('meditation.audio.rain') }}
              </button>
              <button
                :class="['audio-ambient-btn', { active: selectedAmbient === 'forest' }]"
                @click="selectedAmbient = 'forest'; previewAmbient('forest')"
              >
                {{ t('meditation.audio.forest') }}
              </button>
              <button
                :class="['audio-ambient-btn', { active: selectedAmbient === 'ocean' }]"
                @click="selectedAmbient = 'ocean'; previewAmbient('ocean')"
              >
                {{ t('meditation.audio.ocean') }}
              </button>
              <button
                :class="['audio-ambient-btn', { active: selectedAmbient === 'whitenoise' }]"
                @click="selectedAmbient = 'whitenoise'; previewAmbient('whitenoise')"
              >
                {{ t('meditation.audio.whitenoise') }}
              </button>
            </div>
          </div>
          
          <div class="audio-config-section">
            <div class="audio-config-title">{{ t('meditation.audio.volume') }}</div>
            <div class="volume-control">
              <input 
                type="range" 
                v-model.number="masterVolume" 
                min="0" 
                max="1" 
                step="0.1"
                class="volume-slider"
                @input="updatePreviewVolume"
              />
              <span class="volume-label">{{ Math.round(masterVolume * 100) }}%</span>
            </div>
          </div>
        </div>
        
        <!-- Bell Configuration Panel -->
        <div v-if="showBellConfig && !meditationActive" class="bell-config-backdrop" @click="showBellConfig = false"></div>
        <div v-if="showBellConfig && !meditationActive" class="bell-config-panel">
          <div class="bell-config-header">
            <h3 class="bell-config-panel-title">{{ t('meditation.bell.settings') }}</h3>
            <button class="config-close-btn" @click="showBellConfig = false" aria-label="Close bell settings">
              <svg width="20" height="20" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                <path d="M18 6L6 18M6 6l12 12" stroke="currentColor" stroke-width="2" stroke-linecap="round"/>
              </svg>
            </button>
          </div>
          <div class="bell-config-options">
            <div class="bell-dropdown">
              <button class="bell-dropdown-btn" @click="showConfigIntervalDropdown = !showConfigIntervalDropdown">
                {{ bellInterval }} min <span class="dropdown-arrow"></span>
              </button>
              <div v-if="showConfigIntervalDropdown" class="bell-dropdown-menu">
                <button @click="bellInterval = 5; showConfigIntervalDropdown = false">Every 5 min</button>
                <button @click="bellInterval = 10; showConfigIntervalDropdown = false">Every 10 min</button>
                <button @click="bellInterval = 15; showConfigIntervalDropdown = false">Every 15 min</button>
                <button @click="bellInterval = 20; showConfigIntervalDropdown = false">Every 20 min</button>
              </div>
            </div>
            <div class="bell-sound-options">
              <button
                v-for="sound in ['1', '2', '3', '4']"
                :key="sound"
                :class="['bell-sound-btn', { active: bellSound === sound }]"
                @click="selectBellSound(sound)"
              >
                Bell {{ sound }}
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
      <div v-if="meditationActive" class="zen-meditation-overlay">
        <component :is="ANIMATIONS[meditationAnimationIdx]" />
        
        <!-- Audio & Bell Settings Toolbar -->
        <div class="bell-settings-toolbar">
          <!-- Volume Controls -->
          <div class="volume-controls">
            <button 
              :class="['volume-toggle-btn', { muted: isMuted }]"
              @click="toggleMute"
              :aria-label="isMuted ? 'Unmute audio' : 'Mute audio'"
            >
              <svg v-if="!isMuted" width="16" height="16" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                <path d="M11 5L6 9H2v6h4l5 4V5zM15.54 8.46a5 5 0 010 7.07M19.07 4.93a10 10 0 010 14.14" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round"/>
              </svg>
              <svg v-else width="16" height="16" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                <path d="M11 5L6 9H2v6h4l5 4V5zM23 9l-6 6M17 9l6 6" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round"/>
              </svg>
            </button>
            
            <div class="volume-slider-container">
              <button 
                class="volume-slider-toggle"
                @click="showVolumeSlider = !showVolumeSlider"
                :aria-label="'Adjust volume'"
              >
                {{ Math.round(masterVolume * 100) }}%
              </button>
              <div v-if="showVolumeSlider" class="volume-slider-popup">
                <input 
                  type="range" 
                  v-model.number="masterVolume" 
                  min="0" 
                  max="1" 
                  step="0.05"
                  class="volume-slider-vertical"
                  @input="updateMeditationVolume"
                  orient="vertical"
                />
              </div>
            </div>
          </div>
          
          <div class="toolbar-divider"></div>
          <button 
            :class="['bell-toggle-btn', { active: bellEnabled }]"
            @click="bellEnabled = !bellEnabled"
            :aria-label="bellEnabled ? 'Disable interval bells' : 'Enable interval bells'"
          >
            <svg width="16" height="16" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
              <path d="M12 2C10.9 2 10 2.9 10 4C10 4.6 10.2 5.1 10.6 5.5C8 6.1 6 8.3 6 11V16L4 18V19H20V18L18 16V11C18 8.3 16 6.1 13.4 5.5C13.8 5.1 14 4.6 14 4C14 2.9 13.1 2 12 2ZM10 20C10 21.1 10.9 22 12 22C13.1 22 14 21.1 14 20" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round"/>
            </svg>
          </button>
          
          <div v-if="bellEnabled" class="bell-settings">
            <div class="bell-dropdown">
              <button class="bell-dropdown-btn" @click="showIntervalDropdown = !showIntervalDropdown">
                {{ bellInterval }} min <span class="dropdown-arrow"></span>
              </button>
              <div v-if="showIntervalDropdown" class="dropdown-backdrop-inline" @click="showIntervalDropdown = false"></div>
              <div v-if="showIntervalDropdown" class="bell-dropdown-menu">
                <button @click="bellInterval = 5; showIntervalDropdown = false">5 min</button>
                <button @click="bellInterval = 10; showIntervalDropdown = false">10 min</button>
                <button @click="bellInterval = 15; showIntervalDropdown = false">15 min</button>
                <button @click="bellInterval = 20; showIntervalDropdown = false">20 min</button>
              </div>
            </div>
            
            <div class="bell-dropdown">
              <button class="bell-dropdown-btn" @click="showSoundDropdown = !showSoundDropdown">
                Bell {{ bellSound }} <span class="dropdown-arrow"></span>
              </button>
              <div v-if="showSoundDropdown" class="dropdown-backdrop-inline" @click="showSoundDropdown = false"></div>
              <div v-if="showSoundDropdown" class="bell-dropdown-menu">
                <button @click="selectBellSoundFromDropdown('1')">Bell 1</button>
                <button @click="selectBellSoundFromDropdown('2')">Bell 2</button>
                <button @click="selectBellSoundFromDropdown('3')">Bell 3</button>
                <button @click="selectBellSoundFromDropdown('4')">Bell 4</button>
              </div>
            </div>
          </div>
        </div>
        
        <div class="meditation-timer meditation-timer-overlay">
          <div class="timer-display" aria-live="polite" aria-label="Meditation timer">{{ formatTime(meditationSeconds) }}</div>
          <button class="meditation-btn" @click="stopMeditation" aria-label="Stop the current meditation session">{{ t('meditation.stop') }}</button>
        </div>
      </div>
    </template>
  </div>
</template>

<script setup lang="ts">
import MeditationCalendar from './MeditationCalendar.vue'
import SessionNotes from './SessionNotes.vue'
import BreathingExercise from './BreathingExercise.vue'
import EmotionTracker from './EmotionTracker.vue'
import EightfoldPath from './EightfoldPath.vue'
import GratitudeJournal from './GratitudeJournal.vue'
import ZenPhilosophy from './ZenPhilosophy.vue'
import { ref, onMounted, onUnmounted, watch, computed } from 'vue'
import ZenWindAnimation from './ZenWindAnimation.vue'
import ZenWavesAnimation from './ZenWavesAnimation.vue'
import MonkAuth from './MonkAuth.vue'
import { apiRequest } from '../api'
import { useI18n } from 'vue-i18n'

const { t, tm } = useI18n()
// Zen phrases
const phrases = computed(() => tm('phrases') as string[])
const currentPhrase = ref(phrases.value[Math.floor(Math.random() * phrases.value.length)])
let phraseIntervalId: number | undefined

const emit = defineEmits(['meditation-active', 'theme-changed', 'language-changed', 'user-changed'])
const meditationActive = ref(false)

// Watch meditationActive and emit event on change
watch(meditationActive, (val) => {
  emit('meditation-active', val)
})
const meditationSeconds = ref(600) // 10 minutes
const selectedDuration = ref(10) // minutes
const isCustomDuration = ref(false)
const customDurationValue = ref(10)
const customInput = ref<HTMLInputElement | null>(null)
let meditationIntervalId: number | undefined

// Bell settings
const bellEnabled = ref(false)
const bellInterval = ref(10) // minutes
const bellSound = ref('1')
const showBellConfig = ref(false)
const showIntervalDropdown = ref(false)
const showSoundDropdown = ref(false)
const showConfigIntervalDropdown = ref(false)
let lastBellTime = 0
let bellAudioInstance: HTMLAudioElement | null = null

// Audio settings
const selectedTrack = ref('silent') // 'silent', 'track1', 'track2', 'track3', 'track4', 'track5'
const selectedAmbient = ref('none') // 'none', 'rain', 'forest', 'ocean', 'whitenoise'
const showAudioConfig = ref(false)
const masterVolume = ref(0.7)
const isMuted = ref(false)
const showVolumeSlider = ref(false)
let trackAudio: HTMLAudioElement | null = null
let ambientAudio: HTMLAudioElement | null = null
let previewTrackAudio: HTMLAudioElement | null = null
let previewAmbientAudio: HTMLAudioElement | null = null

const showCalendar = ref(false)
const showNotes = ref(false)
const showBreathing = ref(false)
const showEmotions = ref(false)
const showEightfoldPath = ref(false)
const showGratitude = ref(false)
const showPhilosophy = ref(false)
const showDropdown = ref(false)
const dropdownContainer = ref<HTMLElement | null>(null)
const meditations = ref<Array<{ Date: string | { $date: string }, Username?: string, duration?: number, notes?: string }>>([])  
const completedMeditationDuration = ref(0)

const audio = ref<HTMLAudioElement | null>(null)
const alertAudio = ref<HTMLAudioElement | null>(null)

function setRandomPhrase() {
  let next
  do {
    next = phrases.value[Math.floor(Math.random() * phrases.value.length)]
  } while (next === currentPhrase.value && phrases.value.length > 1)
  currentPhrase.value = next
}

function toggleDropdown() {
  showDropdown.value = !showDropdown.value
}

function handleMenuClick(callback: () => void) {
  callback()
  showDropdown.value = false
}

function handleClickOutside(event: MouseEvent) {
  if (dropdownContainer.value && !dropdownContainer.value.contains(event.target as Node)) {
    showDropdown.value = false
  }
}

function selectPresetDuration(duration: number) {
  isCustomDuration.value = false
  selectedDuration.value = duration
}

function enableCustomDuration() {
  isCustomDuration.value = true
  customDurationValue.value = selectedDuration.value
  setTimeout(() => {
    customInput.value?.focus()
    customInput.value?.select()
  }, 50)
}

function applyCustomDuration() {
  if (customDurationValue.value >= 1 && customDurationValue.value <= 180) {
    selectedDuration.value = customDurationValue.value
    isCustomDuration.value = false
  } else {
    customDurationValue.value = Math.max(1, Math.min(180, customDurationValue.value))
  }
}

function cancelCustomDuration() {
  isCustomDuration.value = false
  customDurationValue.value = selectedDuration.value
}

function selectBellSound(sound: string) {
  bellSound.value = sound
  playBellSound() // Preview the sound
}

function selectBellSoundFromDropdown(sound: string) {
  bellSound.value = sound
  showSoundDropdown.value = false
  playBellSound() // Preview the sound
}

async function fetchMeditations() {
  try {
    const res = await apiRequest('/meditations', 'GET')
    meditations.value = res.meditations || []
  } catch (e) {
    meditations.value = []
  }
}

async function fetchUserData() {
  try {
    const res = await apiRequest('/me')
    user.value = res.user
    emit('theme-changed', res.user.theme)
    if (res.user.language) {
      emit('language-changed', res.user.language)
    }
  } catch (e) {
    // Silently handle error - user will be logged out if token invalid
  }
}

function playAlert() {
  if (!alertAudio.value) {
    alertAudio.value = new Audio('/alert.mp3')
  }
  alertAudio.value.currentTime = 0
  alertAudio.value.play()
}

function playBellSound() {
  if (bellAudioInstance) {
    bellAudioInstance.pause()
    bellAudioInstance.currentTime = 0
  }
  
  bellAudioInstance = new Audio(`/bell${bellSound.value}.mp3`)
  bellAudioInstance.volume = 0.5
  bellAudioInstance.play().catch(err => {
    console.log('Bell sound playback failed:', err)
  })
}

async function stopMeditationAudioSmooth() {
  if (audio.value) {
    const fadeDuration = 2 // seconds
    const step = 0.05
    let currentVolume = audio.value.volume
    while (currentVolume > 0.01) {
      currentVolume = Math.max(0, currentVolume - step)
      audio.value.volume = currentVolume
      await new Promise(r => setTimeout(r, fadeDuration * 50))
    }
    audio.value.pause()
    audio.value.currentTime = 0
    audio.value.volume = 1 // reset for next play
  }
}

function playTrack() {
  if (selectedTrack.value !== 'silent') {
    if (trackAudio) {
      trackAudio.pause()
      trackAudio.currentTime = 0
    }
    trackAudio = new Audio(`/${selectedTrack.value}.mp3`)
    trackAudio.loop = true
    trackAudio.volume = isMuted.value ? 0 : masterVolume.value
    trackAudio.play().catch(err => console.error('Track playback failed:', err))
  } else {
  }
}

function playAmbient() {
  if (selectedAmbient.value !== 'none') {
    if (ambientAudio) {
      ambientAudio.pause()
      ambientAudio.currentTime = 0
    }
    ambientAudio = new Audio(`/${selectedAmbient.value}.mp3`)
    ambientAudio.loop = true
    ambientAudio.volume = isMuted.value ? 0 : masterVolume.value * 0.6 // Slightly lower ambient volume
    ambientAudio.play().catch(err => console.error('Ambient playback failed:', err))
  } else {
  }
}

function stopAllAudio() {
  if (audio.value) {
    audio.value.pause()
    audio.value.currentTime = 0
  }
  if (trackAudio) {
    trackAudio.pause()
    trackAudio.currentTime = 0
  }
  if (ambientAudio) {
    ambientAudio.pause()
    ambientAudio.currentTime = 0
  }
}

function toggleMute() {
  isMuted.value = !isMuted.value
  updateMeditationVolume()
}

function updateMeditationVolume() {
  const vol = isMuted.value ? 0 : masterVolume.value
  if (audio.value) audio.value.volume = vol
  if (trackAudio) trackAudio.volume = vol
  if (ambientAudio) ambientAudio.volume = vol * 0.6
}

function previewTrack(track: string) {
  if (previewTrackAudio) {
    previewTrackAudio.pause()
    previewTrackAudio.currentTime = 0
  }
  previewTrackAudio = new Audio(`/${track}.mp3`)
  previewTrackAudio.volume = masterVolume.value
  previewTrackAudio.play().catch(err => console.log('Track preview failed:', err))
  
  // Stop preview after 3 seconds
  setTimeout(() => {
    if (previewTrackAudio) {
      previewTrackAudio.pause()
      previewTrackAudio.currentTime = 0
    }
  }, 3000)
}

function previewAmbient(ambient: string) {
  if (previewAmbientAudio) {
    previewAmbientAudio.pause()
    previewAmbientAudio.currentTime = 0
  }
  previewAmbientAudio = new Audio(`/${ambient}.mp3`)
  previewAmbientAudio.volume = masterVolume.value * 0.6
  previewAmbientAudio.play().catch(err => console.log('Ambient preview failed:', err))
  
  // Stop preview after 3 seconds
  setTimeout(() => {
    if (previewAmbientAudio) {
      previewAmbientAudio.pause()
      previewAmbientAudio.currentTime = 0
    }
  }, 3000)
}

function updatePreviewVolume() {
  if (previewTrackAudio) previewTrackAudio.volume = masterVolume.value
  if (previewAmbientAudio) previewAmbientAudio.volume = masterVolume.value * 0.6
}

async function stopMeditation() {
  meditationActive.value = false
  if (meditationIntervalId) clearInterval(meditationIntervalId)
  await stopMeditationAudioSmooth()
  stopAllAudio()
  playAlert()
}

async function finishMeditation() {
  meditationActive.value = false
  if (meditationIntervalId) clearInterval(meditationIntervalId)
  await stopMeditationAudioSmooth()
  stopAllAudio()
  playAlert()
  
  completedMeditationDuration.value = (selectedDuration.value * 60) - meditationSeconds.value
  showNotes.value = true
}

async function saveSessionNotes(notes: string) {
  try {
    await apiRequest('/meditations', 'POST', {
      Date: new Date().toISOString(),
      duration: Math.round(completedMeditationDuration.value / 60),
      notes
    })
    await fetchMeditations()
    await fetchUserData()
    showNotes.value = false
  } catch (e) {
    // Error saving meditation - UI can handle gracefully
  }
}

async function skipSessionNotes() {
  try {
    await apiRequest('/meditations', 'POST', {
      Date: new Date().toISOString(),
      duration: Math.round(completedMeditationDuration.value / 60),
      notes: ''
    })
    await fetchMeditations()
    await fetchUserData()
    showNotes.value = false
  } catch (e) {
    // Error saving meditation - UI can handle gracefully
  }
}

const ANIMATIONS = [ZenWindAnimation, ZenWavesAnimation]
const meditationAnimationIdx = ref(0)

async function startMeditation() {
  if (meditationActive.value) return
  meditationActive.value = true
  meditationSeconds.value = selectedDuration.value * 60
  lastBellTime = 0
  showVolumeSlider.value = false
  meditationAnimationIdx.value = Math.floor(Math.random() * ANIMATIONS.length)
  meditationIntervalId = window.setInterval(() => {
    if (meditationSeconds.value > 0) {
      meditationSeconds.value--
      
      // Check for interval bells
      if (bellEnabled.value && bellInterval.value > 0) {
        const totalSeconds = selectedDuration.value * 60
        const elapsedSeconds = totalSeconds - meditationSeconds.value
        const elapsedMinutes = elapsedSeconds / 60
        const currentBellMinute = Math.floor(elapsedMinutes / bellInterval.value) * bellInterval.value
        
        if (currentBellMinute > lastBellTime && elapsedMinutes >= bellInterval.value) {
          playBellSound()
          lastBellTime = currentBellMinute
        }
      }
    } else {
      finishMeditation()
    }
  }, 1000)
  playAlert()
  playTrack()
  playAmbient()
}

onMounted(async () => {
  phraseIntervalId = window.setInterval(setRandomPhrase, 10000)
  if (token.value) {
    try {
      await fetchUserData()
      await fetchMeditations()
    } catch (e) {
      // Invalid token or network error - logout user
      localStorage.removeItem('zen_token')
      token.value = null
    }
  }
  document.addEventListener('click', handleClickOutside)
})
onUnmounted(() => {
  if (phraseIntervalId) clearInterval(phraseIntervalId)
  if (meditationIntervalId) clearInterval(meditationIntervalId)
  document.removeEventListener('click', handleClickOutside)
})

function formatTime(sec: number) {
  const m = Math.floor(sec / 60)
  const s = sec % 60
  return `${m.toString().padStart(2, '0')}:${s.toString().padStart(2, '0')}`
}

const today = new Date().toLocaleDateString(undefined, { weekday: 'long', year: 'numeric', month: 'long', day: 'numeric' })

const user = ref<{ username: string, theme?: string, stats?: any, goals?: any } | null>(null)
const token = ref<string | null>(localStorage.getItem('zen_token'))

async function handleAuth(evt: { user: any, token: string }) {
  user.value = evt.user
  token.value = evt.token
  emit('user-changed', evt.user)
  await fetchUserData()
  await fetchMeditations()
  if (evt.user && evt.user.theme) {
    emit('theme-changed', evt.user.theme)
  }
  if (evt.user && evt.user.language) {
    emit('language-changed', evt.user.language)
  }
}

function handleLogout() {
  user.value = null
  token.value = null
  localStorage.removeItem('zen_token')
  emit('user-changed', null)
}

// Optionally, fetch meditations when calendar is opened
watch(showCalendar, (val) => { if (val && user.value) fetchMeditations() })
</script>

